function devNum = findKeyboardDevNumsAtLocation(location)% find the device numbers for the keyboard and the for the keypad...% 11/17/07 DE% 31-March-2011 Rachel Denison%% Updated to take location argument% 2012 September 1devices = PsychHID('Devices');devNum.Keyboard = []; devNum.Keypad = [];for devicecounter = 1:length(devices)    switch location        case '3T' % Stim Mac            if strcmp(devices(devicecounter).product, 'Apple Keyboard') && ...                    strcmp(devices(devicecounter).usageName, 'Keyboard')                devNum.Keyboard = devicecounter;            end            if devices(devicecounter).productID==16385 % forp (button box & TTL pulse)                devNum.Keypad = devicecounter;            end        case {'582D','582J'}            if (strcmp(devices(devicecounter).product, 'Apple Extended USB Keyboard') == 1) && (devices(devicecounter).inputs == 270)                devNum.Keyboard = devicecounter;            end            if (strcmp(devices(devicecounter).product, 'USB Keyboard Hub')) && (devices(devicecounter).inputs == 270)                devNum.Keypad = devicecounter;            end        case 'laptop'            if (strcmp(devices(devicecounter).product, 'Apple Internal Keyboard / Trackpad') == 1) && (strcmp(devices(devicecounter).usageName, 'Keyboard') == 1)                devNum.Keypad = devicecounter;            end        case 'desk'            if (strcmp(devices(devicecounter).product, 'Apple Extended USB Keyboard') == 1) && (strcmp(devices(devicecounter).usageName, 'Keyboard') == 1)                devNum.Keypad = devicecounter;            end        otherwise            error('Testing location not found by findKeyboardDevNumsAtLocation.')    endend